head.script:
		window.onload = init;
alert(document.cookie);
		function init() {
			try{

			   	WebSquare.session.setAttribute("portalUserId","");
			   	WebSquare.session.setAttribute("portalUserCd","");


			   WebSquare.startApplication();

				if(typeof(WebSquare.uiplugin.mapchart) != "undefined"){
					WebSquare.uiplugin.mapchart.prototype.getDataMapPath = function(code) { //맵차트(json) 캐시문제로 overriding
						try {
							var level = 0;
							var ratio = this.options.dataMapRatio_level0;
							if (code.length >= 5) {
								level = 2;
								ratio = this.options.dataMapRatio_level2;
							} else if (code.length >= 2) {
								level = 1;
								ratio = this.options.dataMapRatio_level1;
							}
								var jsonURL = this.options.dataMapPath + "/" + "level" + level + "/" + ratio + "/" + code + ".json";
								jsonURL = WebSquare.BootLoader.getResourcePostfix(jsonURL)
								return jsonURL;
						} catch (e) {
							WebSquare.exception.printStackTrace(e);
						}
					}
				}

				/* if(typeof(WebSquare.uiplugin.fusionchart) != "undefined"){ //웹접근성 라이브러리 추가_항목별현황은 제외(중간 xml화면이 있어서 거기에 등록)
					$w.js("externalJS/FusionCharts3.7/fusioncharts.ext.accessibility-resources-strings-en.js","externalJS/FusionCharts3.7/fusioncharts.ext.accessibility.js");
				} */
			} catch(e) {
				alert(e.message);
			}
		}
